exercises.solution:
function incrementAllValues() {
  return arr.map(value => {
    const paragaphTag = document.createElement('p');
    const paragraphContent = document.createTextNode(value);

    paragraphTag.appendChild(paragraphContent);
    return paragraphTag;
  });
}
